'use client'
import React, { Component, ErrorInfo, ReactNode } from 'react'
import { Card, CardContent, CardHeader, CardTitle } from './ui/card'
import { Button } from './ui/button'
import { ERROR_MESSAGES } from '@/lib/constants/strings'
import { ErrorHandler } from '@/hooks/useErrorBoundary'

interface Props {
  children: ReactNode
  fallback?: ReactNode
  onError?: (error: Error, errorInfo: ErrorInfo) => void
}

interface State {
  hasError: boolean
  error?: Error
  errorInfo?: ErrorInfo
}

export class ErrorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props)
    this.state = { hasError: false }
  }

  static getDerivedStateFromError(error: Error): State {
    return { hasError: true, error }
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    ErrorHandler.logError(error, 'ErrorBoundary')
    this.props.onError?.(error, errorInfo)
    this.setState({ errorInfo })
  }

  render() {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback
      }

      return (
        <Card className="max-w-md mx-auto mt-8">
          <CardHeader>
            <CardTitle className="text-destructive">{ERROR_MESSAGES.SOMETHINWENT_WRONG}</CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground mb-4">
              {ErrorHandler.getUserFriendlyMessage(this.state.error || new Error(ERROR_MESSAGES.UNEXPECTED_ERROR))}
            </p>
            {process.env.NODE_ENV === 'development' && this.state.error && (
              <details className="mb-4">
                <summary className="cursor-pointer text-sm font-medium">Error Details</summary>
                <pre className="mt-2 text-xs bg-muted p-2 rounded overflow-auto">
                  {this.state.error.stack}
                </pre>
              </details>
            )}
            <Button
              onClick={() => this.setState({ hasError: false, error: undefined, errorInfo: undefined })}
              variant="outline"
            >
              {ERROR_MESSAGES.TRY_AGAIN}
            </Button>
          </CardContent>
        </Card>
      )
    }

    return this.props.children
  }
}

'use client'
import React from 'react'
import { Card, CardContent, CardHeader, CardTitle } from './ui/card'
import { Button } from './ui/button'
import { ERROR_MESSAGES } from '@/lib/constants/strings'
import { ErrorHandler, useErrorBoundary } from '@/hooks/useErrorBoundary'

interface ErrorFallbackProps {
  error: Error
  resetError: () => void
  errorInfo?: any
}

export function ErrorFallback({ error, resetError, errorInfo }: ErrorFallbackProps) {

  return (
    <Card className="max-w-md mx-auto mt-8">
      <CardHeader>
        <CardTitle className="text-destructive">{ERROR_MESSAGES.SOMETHINWENT_WRONG}</CardTitle>
      </CardHeader>
      <CardContent>
        <p className="text-sm text-muted-foreground mb-4">
          {ErrorHandler.getUserFriendlyMessage(error)}
        </p>
        {process.env.NODE_ENV === 'development' && (
          <details className="mb-4">
            <summary className="cursor-pointer text-sm font-medium">Error Details</summary>
            <div className="mt-2 space-y-2">
              <pre className="text-xs bg-muted p-2 rounded overflow-auto">
                {error.stack}
              </pre>
              {errorInfo && (
                <pre className="text-xs bg-muted p-2 rounded overflow-auto">
                  {JSON.stringify(errorInfo, null, 2)}
                </pre>
              )}
            </div>
          </details>
        )}
        <Button onClick={resetError} variant="outline">
          {ERROR_MESSAGES.TRY_AGAIN}
        </Button>
      </CardContent>
    </Card>
  )
}

interface FunctionalErrorBoundaryProps {
  children: React.ReactNode
  fallback?: React.ComponentType<ErrorFallbackProps>
  onError?: (error: Error, errorInfo?: any) => void
}

export function FunctionalErrorBoundary({
  children,
  fallback: FallbackComponent = ErrorFallback,
  onError
}: FunctionalErrorBoundaryProps) {
  const { error, errorInfo, resetError, captureError } = useErrorBoundary()

  if (error) {
    onError?.(error, errorInfo)
    return <FallbackComponent error={error} resetError={resetError} errorInfo={errorInfo} />
  }

  return <>{children}</>
}

const FilterButton = memo(({ variant, onClick, children }: FilterButtonProps) => (
  <Button variant={variant} onClick={onClick}>
    {children}
  </Button>
))

FilterButton.displayName = 'FilterButton'

const handleToggle = useCallback(() => {
    try {
      toggleTask(task.id)
    } catch (error) {
      handleError(error as Error, 'toggleTask')
    }
  }, [task.id, toggleTask, handleError])

const handleDelete = useCallback(() => {
    try {
      deleteTask(task.id)
    } catch (error) {
      handleError(error as Error, 'deleteTask')
    }
  }, [task.id, deleteTask, handleError])

  return (
    <Card key={task.id} className={task.completed ? 'opacity-60' : ''}>
      <CardContent className="p-4">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <h3 className={`font-medium ${task.completed ? 'line-through' : ''}`}>
              {task.title}
            </h3>
            {task.description && (
              <p className="text-sm text-muted-foreground mt-1">
                {task.description}
              </p>
            )}
            <div className="flex items-center gap-4 mt-2 text-xs text-muted-foreground">
              <span>{TASK_STATUSES.PRIORITY_LABEL}{task.priority}</span>
              <span>{TASK_STATUSES.CREATED_LABEL}{formatDate(task.createdAt)}</span>
            </div>
          </div>
          <div className="flex gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={handleToggle}
            >
              {task.completed ? TASK_STATUSES.UNDO : TASK_STATUSES.COMPLETE}
            </Button>
            <Button
              variant="destructive"
              size="sm"
              onClick={handleDelete}
            >
              {TASK_STATUSES.DELETE}
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  )
})

