'use client'
import { useState, useCallback } from 'react'

interface ErrorInfo {
  error: Error
  componentStack: string
}

interface UseErrorBoundaryReturn {
  error: Error | null
  errorInfo: ErrorInfo | null
  resetError: () => void
  captureError: (error: Error, errorInfo?: any) => void
}

export function useErrorBoundary(): UseErrorBoundaryReturn {
  const [error, setError] = useState<Error | null>(null)
  const [errorInfo, setErrorInfo] = useState<ErrorInfo | null>(null)

const resetError = useCallback(() => {
    setError(null)
    setErrorInfo(null)
  }, [])

const captureError = useCallback((error: Error, errorInfo?: any) => {
    console.error('Error captured by boundary:', error, errorInfo)
    setError(error)
    setErrorInfo(errorInfo ? { error, componentStack: errorInfo.componentStack || '' } : null)
  }, [])

  return {
    error,
    errorInfo,
    resetError,
    captureError
  }
}

export class ErrorHandler {
  static logError(error: Error, context?: string): void {
    console.group(`Error${context ? ` in ${context}` : ''}`)
    console.error('Error:', error)
    console.error('Stack:', error.stack)
    console.groupEnd()
  }

  static getUserFriendlyMessage(error: Error): string {
    if (error.name === 'NetworkError') {
      return 'Unable to connect to the server. Please check your internet connection.'
    }

    if (error.name === 'TypeError' && error.message.includes('fetch')) {
      return 'Failed to load data. Please try again later.'
    }

    if (error.name === 'QuotaExceededError') {
      return 'Storage limit reached. Please clear some data to continue.'
    }

    return error.message || 'An unexpected error occurred'
  }

  static isRetryableError(error: Error): boolean {

const retryableErrors = [
      'NetworkError',
      'TimeoutError',
      'AbortError'
    ]

    return retryableErrors.includes(error.name) ||
      error.message.includes('network') ||
      error.message.includes('timeout') ||
      error.message.includes('abort')
  }
}

export function useErrorHandler() {

const handleError = useCallback((error: Error, context?: string) => {
    ErrorHandler.logError(error, context)
    return ErrorHandler.getUserFriendlyMessage(error)
  }, [])

const isRetryable = useCallback((error: Error) => {
    return ErrorHandler.isRetryableError(error)
  }, [])

  return {
    handleError,
    isRetryable,
    ErrorHandler
  }
}

