export function useFormValidation<T extends Record<string, any>>(initialState: T, validationRules?: Record<keyof T, (value: any) => boolean>) {
  const [formData, setFormData] = useState<T>(initialState)
  const [errors, setErrors] = useState<Partial<Record<keyof T, string>>>({})

const validateField = useCallback((field: keyof T, value: any): boolean => {
    if (!validationRules || !validationRules[field]) return true
    return validationRules[field](value)
  }, [validationRules])

const updateField = useCallback((field: keyof T, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }))

    if (validationRules && validationRules[field]) {

const isValid = validateField(field, value)
      if (!isValid) {
        setErrors(prev => ({ ...prev, [field]: 'Invalid value' }))
      } else {
        setErrors(prev => {

const newErrors = { ...prev }
          delete newErrors[field]
          return newErrors
        })
      }
    }
  }, [validationRules, validateField])

const resetForm = useCallback(() => {
    setFormData(initialState)
    setErrors({})
  }, [initialState])

const isFormValid = Object.keys(errors).length === 0

  return {
    formData,
    errors,
    updateField,
    resetForm,
    isFormValid,
    validateField
  }
}

export function useTaskOperations() {

const createTask = useCallback((taskData: Omit<Task, 'id' | 'completed' | 'createdAt'>): Task => {
    return {
      ...taskData,
      id: generateId(),
      completed: false,
      createdAt: new Date()
    }
  }, [])

const cloneTask = useCallback((task: Task, overrides?: Partial<Task>): Task => {
    return {
      ...task,
      ...overrides,
      id: generateId(),
      createdAt: new Date()
    }
  }, [])

const errors: string[] = []

    if (!task.title?.trim()) {
      errors.push('Task title is required')
    }

    if (task.title && task.title.length > 100) {
      errors.push('Task title must be less than 100 characters')
    }

    if (task.description && task.description.length > 500) {
      errors.push('Task description must be less than 500 characters')
    }

    return errors
  }, [])

  return {
    createTask,
    cloneTask,
    validateTask
  }
}

export function useDebounce<T>(value: T, delay: number): T {
  const [debouncedValue, setDebouncedValue] = useState<T>(value)

  React.useEffect(() => {

const handler = setTimeout(() => {
      setDebouncedValue(value)
    }, delay)

    return () => {
      clearTimeout(handler)
    }
  }, [value, delay])

  return debouncedValue
}

'use client'
import { useState, useEffect } from 'react'

export function useLocalStorage<T>(key: string, initialValue: T) {
  const [storedValue, setStoredValue] = useState<T>(initialValue)

  useEffect(() => {
    try {

const item = window.localStorage.getItem(key)
      if (item) {
        setStoredValue(JSON.parse(item))
      }
    } catch (error) {
      console.error(`Error reading localStorage key "${key}":`, error)
    }
  }, [key])

const valueToStore = value instanceof Function ? value(storedValue) : value
      setStoredValue(valueToStore)
      window.localStorage.setItem(key, JSON.stringify(valueToStore))
    } catch (error) {
      console.error(`Error setting localStorage key "${key}":`, error)
    }
  }

  return [storedValue, setValue] as const
}

function parseTaskDates(task: any): Task {
