import { create } from 'zustand'
import { persist } from 'zustand/middleware'
import { z } from 'zod'

export interface BaseStoreState<T> {
  data: T | null
  isLoading: boolean
  error: string | null
  lastUpdated: number | null
}

export interface BaseStoreActions<T> {
  setData: (data: T) => void
  setLoading: (loading: boolean) => void
  setError: (error: string | null) => void
  reset: () => void
  updateLastUpdated: () => void
}

export function createBaseStore<T>(
  initialState: BaseStoreState<T>,
  schema: z.ZodType<T>
) {
  return create<BaseStoreState<T> & BaseStoreActions<T>>((set, get) => ({
    ...initialState,
    setData: (data) => {
      const validatedData = schema.parse(data)
      set({ data: validatedData, error: null, lastUpdated: Date.now() })
    },
    setLoading: (isLoading) => set({ isLoading }),
    setError: (error) => set({ error }),
    reset: () => set(initialState),
    updateLastUpdated: () => set({ lastUpdated: Date.now() }),
  }))
}

export function createPersistentStore<T>(
  initialState: BaseStoreState<T>,
  schema: z.ZodType<T>,
  storageKey: string
) {
  return create<BaseStoreState<T> & BaseStoreActions<T>>()(
    persist(
      (set, get) => ({
        ...initialState,
        setData: (data) => {
          const validatedData = schema.parse(data)
          set({ data: validatedData, error: null, lastUpdated: Date.now() })
        },
        setLoading: (isLoading) => set({ isLoading }),
        setError: (error) => set({ error }),
        reset: () => set(initialState),
        updateLastUpdated: () => set({ lastUpdated: Date.now() }),
      }),
      {
        name: storageKey,
        partialize: (state) => ({
          data: state.data,
          lastUpdated: state.lastUpdated,
        }),
      }
    )
  )
}

import { z } from 'zod'

export const AuthUserSchema = z.object({
  accountNo: z.string().min(1, 'Account number is required'),
  email: z.string().email('Invalid email format'),
  role: z.array(z.string()).min(1, 'At least one role is required'),
  exp: z.number().min(0, 'Expiration timestamp must be positive'),
})

export const AuthTokensSchema = z.object({
  accessToken: z.string().min(1, 'Access token is required'),
  refreshToken: z.string().optional(),
  expiresAt: z.number().optional(),
})

export type AuthUser = z.infer<typeof AuthUserSchema>
export type AuthTokens = z.infer<typeof AuthTokensSchema>

export const AuthStateSchema = z.object({
  user: AuthUserSchema.nullable(),
  tokens: AuthTokensSchema.optional(),
  isAuthenticated: z.boolean(),
})

export type AuthState = z.infer<typeof AuthStateSchema>

import { z } from 'zod'

export interface AsyncState<T> {
  data: T | null
  isLoading: boolean
  error: string | null
  isSuccess: boolean
  isError: boolean
}

export interface AsyncActions<T> {
  setData: (data: T) => void
  setLoading: (loading: boolean) => void
  setError: (error: string | Error | null) => void
  reset: () => void
  clearError: () => void
}

export interface PaginationState {
  page: number
  pageSize: number
  total: number
  totalPages: number
}

export interface FilterState {
  search: string
  filters: Record<string, any>
  sortBy: string
  sortOrder: 'asc' | 'desc'
}

export interface AsyncOperationOptions {
  retryCount?: number
  retryDelay?: number
  onSuccess?: (data: any) => void
  onError?: (error: Error) => void
  onSettled?: () => void
}

export function createAsyncState<T>(initialData: T | null = null): AsyncState<T> {
  return {
    data: initialData,
    isLoading: false,
    error: null,
    isSuccess: false,
    isError: false,
  }
}

export function createPaginationState(): PaginationState {
  return {
    page: 1,
    pageSize: 10,
    total: 0,
    totalPages: 0,
  }
}

export function createFilterState(): FilterState {
  return {
    search: '',
    filters: {},
    sortBy: 'id',
    sortOrder: 'desc',
  }
}

export function withAsyncHandling<T>(
  asyncFn: () => Promise<T>,
  options: AsyncOperationOptions = {}
) {
  return async (setLoading: (loading: boolean) => void, setData: (data: T) => void, setError: (error: string | null) => void) => {
    const { retryCount = 0, retryDelay = 1000, onSuccess, onError, onSettled } = options

    setLoading(true)
    setError(null)

    try {
      const result = await asyncFn()
      setData(result)
      onSuccess?.(result)
      return result
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'An unknown error occurred'
      setError(errorMessage)
      onError?.(error instanceof Error ? error : new Error(errorMessage))
      throw error
    } finally {
      setLoading(false)
      onSettled?.()
    }
  }
}

import { z } from 'zod'

export function createSafeAction<T extends z.ZodType<any, any>>(
  schema: T,
  handler: (data: z.infer<T>) => Promise<any> | any
) {
  return async (input: unknown) => {
    try {
      const validatedData = schema.parse(input)
      return await handler(validatedData)
    } catch (error) {
      if (error instanceof z.ZodError) {
        throw new Error(`Validation failed: ${error.errors.map(e => e.message).join(', ')}`)
      }
      throw error
    }
  }
}

export function createRetryableAction<T>(
  action: () => Promise<T>,
  maxRetries = 3,
  delay = 1000
): Promise<T> {
  return new Promise((resolve, reject) => {
    let attempts = 0

    const attempt = () => {
      attempts++
      action()
        .then(resolve)
        .catch((error) => {
          if (attempts < maxRetries) {
            setTimeout(attempt, delay * attempts)
          } else {
            reject(error)
          }
        })
    }

    attempt()
  })
}

export function debounceAction<T extends any[]>(
  action: (...args: T) => void,
  delay = 300
): (...args: T) => void {
  let timeoutId: NodeJS.Timeout
  return (...args: T) => {
    clearTimeout(timeoutId)
    timeoutId = setTimeout(() => action(...args), delay)
  }
}

export function throttleAction<T extends any[]>(
  action: (...args: T) => void,
  limit = 300
): (...args: T) => void {
  let inThrottle: boolean
  return (...args: T) => {
    if (!inThrottle) {
      action(...args)
      inThrottle = true
      setTimeout(() => (inThrottle = false), limit)
    }
  }
}

export function withLocalStorage<T>(
  key: string,
  initialValue: T,
  schema?: z.ZodType<T>
) {
  const getStoredValue = (): T => {
    try {
      const item = window.localStorage.getItem(key)
      if (item === null) return initialValue

      const parsed = JSON.parse(item)
      return schema ? schema.parse(parsed) : parsed
    } catch (error) {
      console.error(`Error reading localStorage key "${key}":`, error)
      return initialValue
    }
  }

  const setStoredValue = (value: T) => {
    try {
      const valueToStore = schema ? schema.parse(value) : value
      window.localStorage.setItem(key, JSON.stringify(valueToStore))
    } catch (error) {
      console.error(`Error setting localStorage key "${key}":`, error)
    }
  }

  const removeStoredValue = () => {
    try {
      window.localStorage.removeItem(key)
    } catch (error) {
      console.error(`Error removing localStorage key "${key}":`, error)
    }
  }

  return {
    getValue: getStoredValue,
    setValue: setStoredValue,
    removeValue: removeStoredValue,
  }
}

