import { create } from 'zustand'
import { persist } from 'zustand/middleware'
import { z } from 'zod'
import { getCookie, setCookie, removeCookie } from '@/lib/cookies'
import { AuthUserSchema, AuthTokensSchema, AuthStateSchema } from './schemas/auth-schema'
import { withAsyncHandling, createAsyncState } from './types/store-types'
import { createSafeAction, withLocalStorage } from './utils/store-utils'

const ACCESS_TOKEN = 'thisisjustarandomstring'

interface AuthState {
  user: z.infer<typeof AuthUserSchema> | null
  tokens: z.infer<typeof AuthTokensSchema> | null
  isLoading: boolean
  error: string | null
  isAuthenticated: boolean
  lastUpdated: number | null
}

interface AuthActions {
  setUser: (user: z.infer<typeof AuthUserSchema> | null) => void
  setTokens: (tokens: z.infer<typeof AuthTokensSchema>) => void
  setAccessToken: (accessToken: string) => void
  resetAccessToken: () => void
  reset: () => void
  setLoading: (loading: boolean) => void
  setError: (error: string | null) => void
  login: (credentials: { email: string; password: string }) => Promise<void>
  logout: () => void
  checkAuth: () => boolean
  refreshAuth: () => Promise<void>
}

const storage = withLocalStorage('auth_state', null, AuthStateSchema.optional())

export const useAuthStore = create<AuthState & AuthActions>()(
  persist(
    (set, get) => {
      const cookieState = getCookie(ACCESS_TOKEN)
      const initToken = cookieState ? JSON.parse(cookieState) : ''

      const asyncLogin = withAsyncHandling(
        async (credentials: { email: string; password: string }) => {
          await new Promise(resolve => setTimeout(resolve, 2000))

          const mockUser = {
            accountNo: 'ACC001',
            email: credentials.email,
            role: ['user'],
            exp: Date.now() + 24 * 60 * 60 * 1000,
          }

          const mockTokens = {
            accessToken: 'mock-access-token',
            refreshToken: 'mock-refresh-token',
            expiresAt: Date.now() + 24 * 60 * 60 * 1000,
          }

          return { user: mockUser, tokens: mockTokens }
        },
        {
          onSuccess: (result) => {
            const { user, tokens } = result
            set((state) => ({
              ...state,
              user: AuthUserSchema.parse(user),
              tokens: AuthTokensSchema.parse(tokens),
              isAuthenticated: true,
              error: null,
              lastUpdated: Date.now(),
            }))
            setCookie(ACCESS_TOKEN, JSON.stringify(tokens.accessToken))
          },
          onError: (error) => {
            set((state) => ({
              ...state,
              error: error.message,
              isAuthenticated: false,
            }))
          },
        }
      )

      return {
        user: null,
        tokens: { accessToken: initToken },
        isLoading: false,
        error: null,
        isAuthenticated: false,
        lastUpdated: null,

        setUser: (user) => {
          const validatedUser = user ? AuthUserSchema.parse(user) : null
          set((state) => ({
            ...state,
            user: validatedUser,
            isAuthenticated: !!validatedUser,
            lastUpdated: Date.now(),
          }))
        },

        setTokens: (tokens) => {
          const validatedTokens = AuthTokensSchema.parse(tokens)
          set((state) => ({
            ...state,
            tokens: validatedTokens,
            isAuthenticated: true,
            lastUpdated: Date.now(),
          }))
        },

        setAccessToken: (accessToken) => {
          set((state) => ({
            ...state,
            tokens: state.tokens ? { ...state.tokens, accessToken } : { accessToken },
            isAuthenticated: !!accessToken,
            lastUpdated: Date.now(),
          }))
          setCookie(ACCESS_TOKEN, JSON.stringify(accessToken))
        },

        resetAccessToken: () => {
          set((state) => ({
            ...state,
            tokens: state.tokens ? { ...state.tokens, accessToken: '' } : null,
            isAuthenticated: false,
            lastUpdated: Date.now(),
          }))
          removeCookie(ACCESS_TOKEN)
        },

        setLoading: (isLoading) => set({ isLoading, lastUpdated: Date.now() }),
        setError: (error) => set({ error, lastUpdated: Date.now() }),

        login: async (credentials) => {
          const { setLoading } = get()
          await asyncLogin(setLoading, () => {}, (error) => get().setError(error))(credentials)
        },

        logout: () => {
          removeCookie(ACCESS_TOKEN)
          storage.removeValue()
          set({
            user: null,
            tokens: null,
            isLoading: false,
            error: null,
            isAuthenticated: false,
            lastUpdated: Date.now(),
          })
        },

        checkAuth: () => {
          const { user, tokens } = get()
          const isValidUser = user && AuthUserSchema.safeParse(user).success
          const isValidToken = tokens && AuthTokensSchema.safeParse(tokens).success
          return !!(isValidUser && isValidToken && tokens?.accessToken)
        },

        refreshAuth: async () => {
          const { checkAuth, tokens } = get()
          if (!checkAuth() || !tokens?.refreshToken) {
            get().logout()
            return
          }

          try {
            await new Promise(resolve => setTimeout(resolve, 1000))
            const newTokens = {
              ...tokens,
              accessToken: 'refreshed-access-token',
              expiresAt: Date.now() + 24 * 60 * 60 * 1000,
            }
            get().setTokens(newTokens)
          } catch (error) {
            get().setError('Failed to refresh authentication')
            get().logout()
          }
        },

        reset: () => {
          removeCookie(ACCESS_TOKEN)
          storage.removeValue()
          set({
            user: null,
            tokens: null,
            isLoading: false,
            error: null,
            isAuthenticated: false,
            lastUpdated: Date.now(),
          })
        },
      }
    },
    {
      name: 'auth-storage',
      partialize: (state) => ({
        user: state.user,
        tokens: state.tokens,
        isAuthenticated: state.isAuthenticated,
        lastUpdated: state.lastUpdated,
      }),
    }
  )
)

