import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query'
import { toast } from 'sonner'
import { z } from 'zod'

export interface QueryOptions<T> {
  queryKey: string[]
  queryFn: () => Promise<T>
  schema?: z.ZodType<T>
  enabled?: boolean
  retry?: number
  retryDelay?: number
  onSuccess?: (data: T) => void
  onError?: (error: Error) => void
}

export interface MutationOptions<T, V = any> {
  mutationFn: (variables: V) => Promise<T>
  schema?: z.ZodType<T>
  invalidateQueries?: string[][]
  onSuccess?: (data: T, variables: V) => void
  onError?: (error: Error, variables: V) => void
  onSettled?: (data: T | undefined, error: Error | null, variables: V) => void
  successMessage?: string
  errorMessage?: string
}

export function useValidatedQuery<T>({
  queryKey,
  queryFn,
  schema,
  enabled = true,
  retry = 1,
  retryDelay = 1000,
  onSuccess,
  onError
}: QueryOptions<T>) {
  return useQuery({
    queryKey,
    queryFn: async () => {
      try {
        const data = await queryFn()
        return schema ? schema.parse(data) : data
      } catch (error) {
        if (error instanceof z.ZodError) {
          throw new Error(`Data validation failed: ${error.errors.map(e => e.message).join(', ')}`)
        }
        throw error
      }
    },
    enabled,
    retry,
    retryDelay,
    onSuccess,
    onError: (error) => {
      toast.error(error.message)
      onError?.(error)
    },
  })
}

export function useValidatedMutation<T, V = any>({
  mutationFn,
  schema,
  invalidateQueries = [],
  onSuccess,
  onError,
  onSettled,
  successMessage,
  errorMessage = 'Operation failed'
}: MutationOptions<T, V>) {
  const queryClient = useQueryClient()

  return useMutation({
    mutationFn: async (variables: V) => {
      try {
        const data = await mutationFn(variables)
        return schema ? schema.parse(data) : data
      } catch (error) {
        if (error instanceof z.ZodError) {
          throw new Error(`Data validation failed: ${error.errors.map(e => e.message).join(', ')}`)
        }
        throw error
      }
    },
    onSuccess: (data, variables) => {
      if (successMessage) {
        toast.success(successMessage)
      }

      invalidateQueries.forEach(queryKey => {
        queryClient.invalidateQueries({ queryKey })
      })

      onSuccess?.(data, variables)
    },
    onError: (error, variables) => {
      toast.error(errorMessage)
      onError?.(error, variables)
    },
    onSettled,
  })
}

export function createQueryKeys(baseKey: string) {
  return {
    all: [baseKey] as const,
    lists: () => [...baseKey, 'list'] as const,
    list: (filters: Record<string, any>) => [...baseKey, 'list', filters] as const,
    details: () => [...baseKey, 'detail'] as const,
    detail: (id: string | number) => [...baseKey, 'detail', id] as const,
  }
}

import { useQuery, useMutation } from '@tanstack/react-query'
import { useAuthStore } from '@/stores/auth-store'
import { AuthUserSchema } from '@/stores/schemas/auth-schema'
import { useValidatedMutation } from './use-query-with-store'

export const authQueryKeys = {
  auth: ['auth'] as const,
  user: () => ['auth', 'user'] as const,
  session: () => ['auth', 'session'] as const,
}

export function useAuthQuery() {
  const { user, isAuthenticated, isLoading } = useAuthStore()

  return useQuery({
    queryKey: authQueryKeys.user(),
    queryFn: async () => {
      if (!user || !isAuthenticated) {
        throw new Error('No authenticated user')
      }
      return AuthUserSchema.parse(user)
    },
    enabled: isAuthenticated && !!user,
    retry: 1,
    staleTime: 5 * 60 * 1000, // 5 minutes
  })
}

export function useLoginMutation() {
  const { login } = useAuthStore()

  return useValidatedMutation({
    mutationFn: async (credentials: { email: string; password: string }) => {
      await login(credentials)
      return { success: true }
    },
    successMessage: 'Login successful',
    errorMessage: 'Login failed',
    invalidateQueries: [authQueryKeys.auth],
  })
}

export function useLogoutMutation() {
  const { logout } = useAuthStore()

  return useValidatedMutation({
    mutationFn: async () => {
      await logout()
      return { success: true }
    },
    successMessage: 'Logged out successfully',
    invalidateQueries: [authQueryKeys.auth],
  })
}

export function useRefreshAuthMutation() {
  const { refreshAuth } = useAuthStore()

  return useValidatedMutation({
    mutationFn: async () => {
      await refreshAuth()
      return { success: true }
    },
    onError: () => {
      // Auto-logout on refresh failure
      useAuthStore.getState().logout()
    },
  })
}

import { useCallback, useMemo } from 'react'
import { create } from 'zustand'
import { z } from 'zod'

export type Selector<T, R> = (state: T) => R

export function createStoreHook<T>(useStore: () => T) {
  return <R = T>(selector?: Selector<T, R>) => {
    const store = useStore()

    if (selector) {
      return useMemo(() => selector(store), [selector, store])
    }

    return store
  }
}

export function createSafeSelector<T extends Record<string, any>>(
  schema: z.ZodType<T>
) {
  return <R = T>(selector: (state: T) => R) => {
    return (state: unknown): R => {
      try {
        const validatedState = schema.parse(state)
        return selector(validatedState)
      } catch (error) {
        if (error instanceof z.ZodError) {
          console.error('State validation failed:', error.errors)
          throw new Error('Invalid state structure')
        }
        throw error
      }
    }
  }
}

export function useStoreSubscription<T, R>(
  useStore: () => T,
  selector: Selector<T, R>,
  equalityFn?: (a: R, b: R) => boolean
): R {
  const store = useStore()

  return useMemo(() => {
    return selector(store)
  }, [store, selector, equalityFn])
}

export function createMemoizedSelector<T>() {
  const cache = new WeakMap()

  return <R>(selector: (state: T) => R): ((state: T) => R) => {
    if (cache.has(selector)) {
      return cache.get(selector)
    }

    const memoized = (state: T): R => {
      const key = JSON.stringify(state, Object.keys(state).sort())

      if (!cache.has(selector)) {
        cache.set(selector, new Map())
      }

      const selectorCache = cache.get(selector)

      if (selectorCache.has(key)) {
        return selectorCache.get(key)
      }

      const result = selector(state)
      selectorCache.set(key, result)

      return result
    }

    cache.set(selector, memoized)
    return memoized
  }
}
