// Test performance improvements by simulating rapid state changes
const testPerformance = () => {
  console.log('Testing performance optimizations...');

  // Simulate many rapid state updates
  const updates = [];
  for (let i = 0; i < 1000; i++) {
    updates.push({
      id: `task-${i}`,
      title: `Task ${i}`,
      description: `Description ${i}`,
      completed: i % 2 === 0,
      priority: ['low', 'medium', 'high'][i % 3],
      createdAt: new Date()
    });
  }

  console.log(`Performance test with ${updates.length} tasks completed`);
  return updates.length;
};

const result = testPerformance();
console.log(`Performance test result: ${result} tasks processed`);
