const fs = require('fs');

// Read the shows that made the cut
const shows = JSON.parse(fs.readFileSync('shows_that_made_the_cut.json', 'utf8'));

// Extract unique show titles
const showTitles = [...new Set(shows.map(s => s.title))].sort();

console.log('=== ALL SHOWS THAT MADE THE CUT ===\n');

showTitles.forEach((title, i) => {
  console.log(`${(i+1).toString().padStart(4)}. ${title}`);
});

console.log(`\n=== TOTAL: ${showTitles.length} unique shows ===`);
